mapper.select:
	select 

  from t_living_payment lp
  	left join t_user u on lp.user_id = u.u_id
  	left join t_specification sp on sp.u_id = lp.spec_id
  where lp.delete_status = 1

      and lp.u_id = #{uId,jdbcType=INTEGER}


      and lp.add_date = #{addDate,jdbcType=TIMESTAMP}


      and lp.last_modify_date = #{lastModifyDate,jdbcType=TIMESTAMP}


      and lp.user_id.u_id = #{userId.uId,jdbcType=INTEGER}


      and lp.name =  #{name,jdbcType=VARCHAR}


      and lp.spec_id = #{specId,jdbcType=INTEGER}


      and lp.share_info = #{shareInfo,jdbcType=VARCHAR}
