mapper.select:
	select 

  from t_insterest_thing_discuss
  where delete_status = 1

      and u_id = #{uId,jdbcType=INTEGER}


      and add_date = #{addDate,jdbcType=TIMESTAMP}


      and last_modify_date = #{lastModifyDate,jdbcType=TIMESTAMP}


      and insterest_thing_id = #{insterestThingId,jdbcType=INTEGER}


      and user_id = #{userId.uId,jdbcType=INTEGER}


      and parent_discuss_id =  #{parentDiscussId,jdbcType=INTEGER}


      and comment_info = #{commentInfo,jdbcType=VARCHAR}

    order by add_date
