mapper.select:
	select 

  from t_user
  where delete_status = 1

      and u_id = #{uId,jdbcType=INTEGER}


      and add_date = #{addDate,jdbcType=TIMESTAMP}


      and last_modify_date = #{lastModifyDate,jdbcType=TIMESTAMP}


      and user_name = #{userName,jdbcType=VARCHAR}


      and phone = #{phone,jdbcType=VARCHAR}


      and email = #{email,jdbcType=VARCHAR}


      and person_introduction = #{personIntroduction,jdbcType=VARCHAR}


      and feel_word = #{feelWord,jdbcType=VARCHAR}

    order by add_date desc
